import React, {useEffect, useState} from 'react';
import {
  View,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  ImageBackground,
  ActivityIndicator,
} from 'react-native';
import {Text, useTheme, Appbar} from 'react-native-paper';
import firestore from '@react-native-firebase/firestore';

const FALLBACK_CUISINES = [
  {
    id: '1',
    name: 'Chinese',
    imageUrl:
      'https://imgs.search.brave.com/OHBQeZqm9TLTxquMmtW-U5cwmIjUlaZXTaRnHite2jw/rs:fit:860:0:0:0/g:ce/aHR0cHM6Ly9jZG4u/dmVjdG9yc3RvY2su/Y29tL2kvcHJldmll/dy0xeC81My83MC9j/aGluZXNlLWZvb2Qt/cmljZS1jb2xvci1m/bGF0LWljb24tdmVj/dG9yLTk1NjUzNzAu/anBn',
  },
  {
    id: '2',
    name: 'South Indian',
    imageUrl:
      'https://th.bing.com/th/id/OIP.b8apmr7MEf5KEcBEDBipIAHaFP?w=280&h=198&c=7&r=0&o=5&dpr=1.5&pid=1.7',
  },
  {
    id: '3',
    name: 'Beverages',
    imageUrl:
      'data:image/jpeg;base64,',
  },
  {
    id: '4',
    name: 'North Indian',
    imageUrl:
      'data:image/jpeg;base64,',
  },
  {
    id: '5',
    name: 'Italian',
    imageUrl:
      'https://th.bing.com/th/id/OIP.kTjQrVlDgEGregeDQ2I9IwHaHa?w=178&h=180&c=7&r=0&o=5&dpr=1.5&pid=1.7',
  },
  {
    id: '6',
    name: 'Fast Food',
    imageUrl:
      'https://static.vecteezy.com/system/resources/previews/036/346/173/non_2x/fast-food-logo-design-vector.jpg',
  },
];

const CuisineScreen = ({navigation}) => {
  const theme = useTheme();
  const [cuisines, setCuisines] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = firestore()
      .collection('cuisines')
      .onSnapshot(
        querySnapshot => {
          const cuisinesData = [];
          querySnapshot.forEach(documentSnapshot => {
            cuisinesData.push({
              ...documentSnapshot.data(),
              id: documentSnapshot.id,
            });
          });
          setCuisines(
            cuisinesData.length > 0 ? cuisinesData : FALLBACK_CUISINES,
          );
          setLoading(false);
        },
        error => {
          console.error('Error fetching cuisines: ', error);
          setCuisines(FALLBACK_CUISINES);
          setLoading(false);
        },
      );

    return () => unsubscribe();
  }, []);

  const renderCuisineItem = ({item}) => (
    <TouchableOpacity
      style={styles.cuisineItemContainer}
      onPress={() =>
        navigation.navigate('FoodList', {
          cuisineId: item.id,
          cuisineName: item.name,
        })
      }>
      <ImageBackground
        source={{
          uri:
            item.imageUrl ||
            'https://via.placeholder.com/150/CCCCCC/FFFFFF?Text=NoImage',
        }}
        style={styles.cuisineItem}
        imageStyle={styles.cuisineImageStyle}>
        <View style={styles.overlay}>
          <Text style={styles.cuisineName}>{item.name}</Text>
        </View>
      </ImageBackground>
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.loaderContainer}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
      </View>
    );
  }

  return (
    <View
      style={[styles.container, {backgroundColor: theme.colors.background}]}>
      <Appbar.Header>
        <Appbar.Content title="Cuisine" subtitle="Select Cuisine" />
      </Appbar.Header>
      <FlatList
        data={cuisines}
        renderItem={renderCuisineItem}
        keyExtractor={item => item.id}
        numColumns={2}
        contentContainerStyle={styles.listContainer}
        ListEmptyComponent={
          <Text style={styles.emptyText}>No cuisines available.</Text>
        }
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  loaderContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  listContainer: {
    padding: 8,
  },
  cuisineItemContainer: {
    flex: 1,
    margin: 8,
    height: 150,
    borderRadius: 10,
    overflow: 'hidden',
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: {width: 0, height: 2},
    shadowOpacity: 0.1,
    shadowRadius: 2,
  },
  cuisineItem: {
    flex: 1,
    justifyContent: 'flex-end',
    alignItems: 'center',
  },
  cuisineImageStyle: {
    borderRadius: 10,
  },
  overlay: {
    backgroundColor: 'rgba(0,0,0,0.4)',
    width: '100%',
    paddingVertical: 10,
  },
  cuisineName: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
  },
  emptyText: {
    textAlign: 'center',
    marginTop: 50,
    fontSize: 16,
  },
});

export default CuisineScreen;
